<script>
import $ from 'jquery'
import { CSSPlugin, TimelineMax, Power4 } from 'gsap'

export default {
  data () {
    return {
      contents: ['Apaixonado por tecnologias', 'Analista e Desenvolvedor de Softwares', 'Desenvolvedor FullStack', 'Analista de Sistemas', 'GestÃ£o de Equipe'],
      currentSentence: 0,
      characterNumberCurrentSentence: 0,
      handleSetInterval: '',
      elementText: {}
    }
  },
  mounted () {
    this.initShowMyName()
    this.initSpecification()
  },
  methods: {
    initShowMyName () {
      CSSPlugin.useSVGTransformAttr = true
      const tl = new TimelineMax({ repeat: 0 })
      const path = 'svg *'
      const staggerVal = 0.019
      const duration = 2

      $.each($(path), function (i, el) {
        tl.set($(this), {
          x: '+=' + getRandom(-500, 500),
          y: '+=' + getRandom(-500, 500),
          rotation: '+=' + getRandom(-720, 720),
          scale: 0,
          opacity: 0
        })
      })

      const staggerOptsTo = {
        x: 0,
        y: 0,
        opacity: 1,
        scale: 1,
        rotation: 0,
        ease: Power4.easeInOut
      }

      tl.staggerTo(path, duration, staggerOptsTo, staggerVal)
      var $svg = $('svg')
      $svg.hover(
        function () {
          tl.timeScale(0.15)
        },
        function () {
          tl.timeScale(1)
        })

      function getRandom (min, max) {
        return Math.random() * (max - min) + min
      }
    },
    typingEffect () {
      const text = this.contents[this.currentSentence].substring(0, this.characterNumberCurrentSentence + 1)
      this.elementText.innerHTML = text
      this.characterNumberCurrentSentence++

      if (text === this.contents[this.currentSentence]) {
        clearInterval(this.handleSetInterval)
        setTimeout(() => {
          this.handleSetInterval = setInterval(this.delete, 50)
        }, 2000)
      }
    },
    delete () {
      const text = this.contents[this.currentSentence].substring(0, this.characterNumberCurrentSentence - 1)
      this.elementText.innerHTML = text
      this.characterNumberCurrentSentence--

      if (text === '') {
        clearInterval(this.handleSetInterval)
        if (this.currentSentence === (this.contents.length - 1)) {
          this.currentSentence = 0
        } else {
          this.currentSentence++
        }

        this.characterNumberCurrentSentence = 0
        setTimeout(() => {
          this.handleSetInterval = setInterval(this.typingEffect, 100)
        }, 200)
      }
    },
    initSpecification () {
      this.elementText = document.querySelector('.text')
      this.handleSetInterval = setInterval(this.typingEffect, 100)
    }
  }
}
</script>

